package com.pdmtaller2.a0104923_GerardoMejia.ui.componentsimport androidx.compose.foundation.backgroundimport androidx.compose.foundation.clickableimport androidx.compose.foundation.layout.Arrangementimport androidx.compose.foundation.layout.Boximport androidx.compose.foundation.layout.Columnimport androidx.compose.foundation.layout.fillMaxSizeimport androidx.compose.foundation.layout.paddingimport androidx.compose.foundation.layout.sizeimport androidx.compose.foundation.shape.RoundedCornerShapeimport androidx.compose.material3.Cardimport androidx.compose.material3.CardDefaultsimport androidx.compose.material3.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.draw.blurimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.layout.ContentScaleimport androidx.compose.ui.text.font.FontWeightimport androidx.compose.ui.tooling.preview.Previewimport androidx.compose.ui.unit.dpimport androidx.navigation.NavControllerimport coil3.compose.AsyncImageimport com.pdmtaller2.a0104923_GerardoMejia.data.Restaurantimport com.pdmtaller2.a0104923_GerardoMejia.data.restaurants@Composablefun RestaurantCard(restaurant: Restaurant, navController: NavController) {    Card(        modifier = Modifier            .size(120.dp)            .padding(end = 8.dp)            .clickable {                navController.navigate("restaurantDetail/${restaurant.id}")            },        shape = RoundedCornerShape(12.dp),        elevation = CardDefaults.cardElevation(4.dp)    ) {        Box(            modifier = Modifier.fillMaxSize()        ) {            AsyncImage(                model = restaurant.imgURL,                contentDescription = restaurant.name,                modifier = Modifier.fillMaxSize().blur(radius = 14.dp),                contentScale = ContentScale.Crop,            )            Box(                modifier = Modifier                    .fillMaxSize()                    .background(                        Color.Black.copy(alpha = 0.4f)                    )            )            Text(                text = restaurant.name,                modifier = Modifier.align(Alignment.Center),                color = Color.White,                fontWeight = FontWeight.Bold            )        }    }}