package com.pdmtaller2.a0104923_GerardoMejia.ui.componentsimport androidx.compose.foundation.clickableimport androidx.compose.foundation.layout.Arrangementimport androidx.compose.foundation.layout.Columnimport androidx.compose.foundation.layout.fillMaxSizeimport androidx.compose.foundation.layout.paddingimport androidx.compose.foundation.layout.sizeimport androidx.compose.foundation.shape.RoundedCornerShapeimport androidx.compose.material3.Cardimport androidx.compose.material3.CardDefaultsimport androidx.compose.material3.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.tooling.preview.Previewimport androidx.compose.ui.unit.dpimport androidx.navigation.NavControllerimport com.pdmtaller2.a0104923_GerardoMejia.data.Restaurantimport com.pdmtaller2.a0104923_GerardoMejia.data.restaurants@Composablefun RestaurantCard(restaurant: Restaurant, navController: NavController) {    Card(        modifier = Modifier            .size(120.dp)            .padding(end = 8.dp)            .clickable {                navController.navigate("restaurantDetail/${restaurant.id}")            },        shape = RoundedCornerShape(12.dp),        elevation = CardDefaults.cardElevation(4.dp)    ) {        Column(            modifier = Modifier                .fillMaxSize()                .padding(8.dp),            verticalArrangement = Arrangement.Center,            horizontalAlignment = Alignment.CenterHorizontally        ) {            Text(text = restaurant.name)        }    }}