package com.pdmtaller2.a0104923_GerardoMejia.ui.componentsimport androidx.compose.foundation.layout.Columnimport androidx.compose.foundation.layout.PaddingValuesimport androidx.compose.foundation.layout.paddingimport androidx.compose.foundation.lazy.LazyRowimport androidx.compose.foundation.lazy.itemsimport androidx.compose.material3.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.compose.ui.text.TextStyleimport androidx.compose.ui.text.font.FontWeightimport androidx.compose.ui.unit.dpimport androidx.compose.ui.unit.spimport androidx.navigation.NavControllerimport com.pdmtaller2.a0104923_GerardoMejia.data.Restaurant@Composablefun RestaurantCategorySection(    title: String,    restaurants: List<Restaurant>,    navController: NavController) {    Column(modifier = Modifier.padding(vertical = 8.dp)) {        Text(            text = title,            modifier = Modifier.padding(start = 16.dp, bottom = 8.dp),            style = TextStyle(                fontSize = 20.sp,                fontWeight = FontWeight.SemiBold            ))        LazyRow(contentPadding = PaddingValues(horizontal = 16.dp)) {            items(restaurants) { restaurant ->                RestaurantCard(restaurant, navController)            }        }    }}