package com.pdmtaller2.a0104923_GerardoMejia.ui.navigationimport androidx.compose.runtime.Composableimport androidx.compose.runtime.mutableStateListOfimport androidx.compose.runtime.rememberimport androidx.navigation.NavControllerimport androidx.navigation.NavHostControllerimport androidx.navigation.compose.NavHostimport androidx.navigation.compose.composableimport com.pdmtaller2.a0104923_GerardoMejia.data.Dishimport com.pdmtaller2.a0104923_GerardoMejia.data.restaurantsimport com.pdmtaller2.a0104923_GerardoMejia.ui.screens.ListScreenimport com.pdmtaller2.a0104923_GerardoMejia.ui.screens.OrdersScreenimport com.pdmtaller2.a0104923_GerardoMejia.ui.screens.RestaurantDetailScreenimport com.pdmtaller2.a0104923_GerardoMejia.ui.screens.SearchScreenimport androidx.compose.runtime.getValueimport androidx.compose.runtime.setValue@Composablefun AppNavigation(navController: NavHostController){    var orderDishes = remember { mutableListOf<Dish>() }    NavHost(navController = navController, startDestination = Screen.ListsScreen.route) {        composable(Screen.ListsScreen.route) {            ListScreen(navController)        }        composable(Screen.SearchScreen.route) {            SearchScreen(navController)        }        composable(Screen.OrdersScreen.route) {            OrdersScreen(navController, orderDishes)        }        composable(            route = Screen.RestaurantDetailScreen.route        ) { backStackEntry ->            val restaurantId = backStackEntry.arguments?.getString("restaurantId")?.toIntOrNull()            val restaurant = restaurants.find { it.id == restaurantId }            restaurant?.let {                RestaurantDetailScreen(it, navController, orderDishes)            }        }    }}