package com.pdmtaller2.a0104923_GerardoMejia.ui.navigationsealed class Screen(val route: String) {    object ListsScreen : Screen("lists")    object OrdersScreen : Screen("orders")    object SearchScreen : Screen("search")    object RestaurantDetailScreen : Screen("restaurantDetail/{restaurantId}") {        fun createRoute(restaurantId: Int) = "restaurantDetail/$restaurantId"    }}