package com.pdmtaller2.a0104923_GerardoMejia.ui.screensimport androidx.compose.foundation.layout.Columnimport androidx.compose.foundation.layout.fillMaxSizeimport androidx.compose.foundation.layout.paddingimport androidx.compose.material3.ExperimentalMaterial3Apiimport androidx.compose.material3.Scaffoldimport androidx.compose.material3.Textimport androidx.compose.material3.TopAppBarimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.compose.ui.unit.dpimport androidx.navigation.NavControllerimport com.pdmtaller2.a0104923_GerardoMejia.helpers.getRestaurantsByCategoryimport com.pdmtaller2.a0104923_GerardoMejia.ui.components.RestaurantCategorySection@OptIn(ExperimentalMaterial3Api::class)@Composablefun ListScreen(navController: NavController) {    Scaffold(        topBar = {            TopAppBar(                title = { Text("Restaurantes") },                actions = {                }            )        }    ) { innerPadding ->        Column(            modifier = Modifier                .padding(innerPadding)                .fillMaxSize()        ) {            RestaurantCategorySection(                title = "Pizzas", restaurants =                    getRestaurantsByCategory("Pizzas"), navController = navController            )            RestaurantCategorySection(                title = "Comida Mexicana", restaurants =                    getRestaurantsByCategory("Mexicana"), navController = navController            )        }    }}