package com.pdmtaller2.a0104923_GerardoMejia.ui.screensimport androidx.compose.foundation.layout.Columnimport androidx.compose.foundation.layout.Spacerimport androidx.compose.foundation.layout.fillMaxSizeimport androidx.compose.foundation.layout.heightimport androidx.compose.foundation.layout.paddingimport androidx.compose.foundation.layout.safeContentPaddingimport androidx.compose.foundation.rememberScrollStateimport androidx.compose.foundation.verticalScrollimport androidx.compose.material3.ExperimentalMaterial3Apiimport androidx.compose.material3.Scaffoldimport androidx.compose.material3.Textimport androidx.compose.material3.TopAppBarimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.compose.ui.text.TextStyleimport androidx.compose.ui.text.font.FontWeightimport androidx.compose.ui.unit.dpimport androidx.compose.ui.unit.spimport androidx.navigation.NavControllerimport com.pdmtaller2.a0104923_GerardoMejia.helpers.getRestaurantsByCategoryimport com.pdmtaller2.a0104923_GerardoMejia.ui.components.RestaurantCategorySection@OptIn(ExperimentalMaterial3Api::class)@Composablefun ListScreen(navController: NavController) {    Scaffold(        topBar = {            TopAppBar(                title = { Text("Restaurantes", style = TextStyle(fontSize = 30.sp, fontWeight =                    FontWeight.Bold)) },                actions = {                }            )        }    ) { innerPadding ->        Column(            modifier = Modifier                .padding(innerPadding)                .fillMaxSize().verticalScroll(rememberScrollState())            ) {            RestaurantCategorySection(                title = "Comida Italiana", restaurants =                    getRestaurantsByCategory("Italiana"), navController = navController            )            RestaurantCategorySection(                title = "Comida Mexicana", restaurants =                    getRestaurantsByCategory("Mexicana"), navController = navController            )            RestaurantCategorySection(                title = "Comida Rápida", restaurants =                    getRestaurantsByCategory("Comida Rápida"), navController = navController            )            RestaurantCategorySection(                title = "Para Vegetarianos", restaurants =                    getRestaurantsByCategory("Vegetariano"), navController = navController            )            RestaurantCategorySection(                title = "Para Amantes de la Carne", restaurants =                    getRestaurantsByCategory("Carnes"), navController = navController            )            Spacer(modifier = Modifier.height(80.dp))        }    }}