package com.pdmtaller2.a0104923_GerardoMejia.ui.screensimport androidx.compose.foundation.layout.Columnimport androidx.compose.foundation.layout.Spacerimport androidx.compose.foundation.layout.heightimport androidx.compose.foundation.layout.paddingimport androidx.compose.material3.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.compose.ui.unit.dpimport com.pdmtaller2.a0104923_GerardoMejia.data.Restaurant@Composablefun RestaurantDetailScreen(restaurant: Restaurant) {    Column(modifier = Modifier.padding(16.dp)) {        Text(restaurant.name)        Spacer(modifier = Modifier.height(8.dp))        Text(restaurant.description)        Spacer(modifier = Modifier.height(8.dp))        Text("Ubicación: ${restaurant.location}")        Spacer(modifier = Modifier.height(8.dp))        Text("Categorías: ${restaurant.categories.joinToString()}")        Spacer(modifier = Modifier.height(16.dp))        Text("Menú:")        restaurant.menu.forEach { dish ->            Text("• ${dish.name}: ${dish.description}")        }    }}